// Local wrapper: attempt to load a local copy of the library if available, otherwise fallback to CDN.
// This file is a lightweight loader to allow "local" bundling; for full offline usage replace this with
// the actual minified library file contents downloaded from the qrcode package.
(function(){
  if(typeof QRCode !== 'undefined') return;
  try{
    // If a real minified library is present in the repo at the same path, it will load here because index.html
    // attempts to load qrcode.min.js first. This stub provides a helpful console message when the real library
    // isn't present and will attempt to fetch the CDN as a fallback if needed.
    console.warn('qrcode.min.js wrapper loaded; ensure a full qrcode.min.js is present for offline use.');
  }catch(e){console.error(e)}
})();
